import React, { useState, useContext } from "react";
import {
  Form,
  Button,
  Modal,
  InputGroup,
  FormControl,
  OverlayTrigger,
  Tooltip,
} from "react-bootstrap";
import AuthContext from "../../Context/AuthContext";
import { useNavigate } from "react-router-dom";

const CreateEditGoalForm = ({ creating, title, amount, saved }) => {
  const [isCreating, setIsCreating] = useState(creating);
  let { authTokens, logoutUser } = useContext(AuthContext);
  const history = useNavigate();

  const [validated, setValidated] = useState(false);

  const createTransactionForGoal = async (event) => {
    let response = await fetch("/api/transactions/create/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + String(authTokens.access),
      },
      body: JSON.stringify({
        amount: event.target.elements.formSaved.value,
        category: "goal",
        receiver: event.target.elements.formReceiver.value,
        transaction_way: "O",
        date: event.target.elements.formDate.value,
      }),
    });
    let data = await response.json();
    if (response.status === 200) {
      //history("/");
      //For now i use this because of time but its not a good solution to
      //update the react view when create new object
      window.location.reload();
    } else if (response.statusText === "Unauthorized") {
      logoutUser();
    }
  };

  const createGoal = async (event) => {
    let response = await fetch("/api/goals/create/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + String(authTokens.access),
      },
      body: JSON.stringify({
        amount: event.target.elements.formAmount.value,
        saved: event.target.elements.formSaved.value,
        title: event.target.elements.formReceiver.value,
      }),
    });
    let data = await response.json();
    if (response.status === 200) {
      //history("/");
      await createTransactionForGoal(event);
    } else if (response.statusText === "Unauthorized") {
      logoutUser();
    }
  };

  const editGoal = async (event) => {
    let response = await fetch("/api/goals/create/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + String(authTokens.access),
      },
      body: JSON.stringify({
        amount: event.target.elements.formAmount.value,
        saved: event.target.elements.formSaved.value,
        title: event.target.elements.formReceiver.value,
      }),
    });
    let data = await response.json();
    if (response.status === 200) {
      //history("/");
      await createTransactionForGoal(event);
    } else if (response.statusText === "Unauthorized") {
      logoutUser();
    }
  };

  const handleSubmit = async (event) => {
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    } else {
      if (isCreating) {
        createGoal(event);
      } else {
        editGoal(event);
      }
    }
    setValidated(true);
    handleCloseForm();
  };

  const handleCloseForm = (event) => {
    event.preventDefault();
    event.stopPropagation();
    setValidated(false);
  };

  return (
    <div className="w-100">
      <Form
        noValidate
        validated={validated}
        onSubmit={(e) => handleSubmit(e)}
        onReset={(e) => handleCloseForm(e)}
      >
        <div>
          <Form.Group className="mb-3" controlId="formReceiver">
            <Form.Label>Goal Title </Form.Label>
            {!isCreating ? (
              <Form.Control
                required
                type="text"
                placeholder="Enter the title"
                value={title}
              />
            ) : (
              <Form.Control
                required
                type="text"
                placeholder="Enter the title"
              />
            )}

            <Form.Control.Feedback type="invalid">
              Please enter a receiver.
            </Form.Control.Feedback>
            <Form.Text></Form.Text>
          </Form.Group>
        </div>
        <Form.Group className="mb-3" controlId="formAmount">
          <Form.Label>Amount</Form.Label>
          <InputGroup className="mb-3">
            <InputGroup.Text>$</InputGroup.Text>
            {isCreating ? (
              <FormControl
                required
                type="number"
                min="0.00"
                max="10000.00"
                step="0.01"
                placeholder="Enter amount"
                value={amount}
              />
            ) : (
              <FormControl
                required
                type="number"
                min="0.00"
                max="10000.00"
                step="0.01"
                placeholder="Enter amount"
              />
            )}

            <Form.Control.Feedback type="invalid">
              Please enter a amount.
            </Form.Control.Feedback>
          </InputGroup>
        </Form.Group>
        <div>
          <Form.Group className="mb-3" controlId="formSaved">
            <Form.Label>Saved</Form.Label>
            <InputGroup className="mb-3">
              <InputGroup.Text>$</InputGroup.Text>
              {isCreating ? (
                <FormControl
                  required
                  type="number"
                  min="0.00"
                  max="10000.00"
                  step="0.01"
                  placeholder="Enter amount saved"
                  value={saved}
                />
              ) : (
                <FormControl
                  required
                  type="number"
                  min="0.00"
                  max="10000.00"
                  step="0.01"
                  placeholder="Enter amount saved"
                />
              )}

              <Form.Control.Feedback type="invalid">
                Please enter a amount saved.
              </Form.Control.Feedback>
            </InputGroup>
          </Form.Group>
        </div>
        <div>
          <Form.Group className="mb-3" controlId="formDate">
            <Form.Label>Date </Form.Label>
            <Form.Control required type="date" placeholder="Enter the date" />
            <Form.Control.Feedback type="invalid">
              Please choose a date.
            </Form.Control.Feedback>
            <Form.Text></Form.Text>
          </Form.Group>
        </div>
        <div>
          <div className="d-flex justify-content-center">
            <Button size="md" variant="primary" type="submit">
              {isCreating ? "Add Goal" : "Edit Goal"}
            </Button>
          </div>
        </div>
      </Form>
    </div>
  );
};

export default CreateEditGoalForm;
