import { Modal } from "bootstrap";
import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";

const AddTransacGoal = ({ goal }) => {
  const [isGoal, setIsGoal] = useState(goal);
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const submitForm = () => {
    if (isGoal) {
      console.log("Added Goal");
    } else {
      console.log("Added Transaction");
    }
  };
  return (
    <div>
      <div className="row mt-2 mb-5 col-lg-12 col-md-3 col-sm-5 d-flex justify-content-center">
        <Button size="lg" variant="outline-info" onClick={handleShow}>
          Add Goal
        </Button>
      </div>
      <Modal show={show} onHide={handleClose}>
        {isGoal ? (
          <Modal.Header closeButton>
            <Modal.Title>Add new goal</Modal.Title>
          </Modal.Header>
        ) : (
          <Modal.Header closeButton>
            <Modal.Title>Add new transaction</Modal.Title>
          </Modal.Header>
        )}
        <Modal.Body>
          <Form onSubmit={submitForm}>
            {isGoal ? (
              <div>
                <Form.Group className="mb-3" controlId="formBasicName">
                  <Form.Label>Name </Form.Label>
                  <Form.Control type="name" placeholder="Enter your name" />
                  <Form.Text></Form.Text>
                </Form.Group>
              </div>
            ) : (
              <div></div>
            )}
            <Form.Group className="mb-3" controlId="formBasicEmail">
              <Form.Label>Email address</Form.Label>
              <Form.Control type="email" placeholder="Enter email" />
              <Form.Text className="text-muted">
                {isGoal ? "We'll never share your email with anyone else." : ""}
              </Form.Text>
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicPassword">
              <Form.Label>Password</Form.Label>
              <Form.Control type="password" placeholder="Password" />
              <Form.Text className="text-muted">
                {isGoal ? "Needs to have at least 8 characters." : ""}
              </Form.Text>
            </Form.Group>
            {isGoal ? (
              <div>
                <Form.Group className="mb-3" controlId="formBasicCheckbox">
                  <Form.Check
                    type="checkbox"
                    label="I accept terms and conditions"
                  />
                </Form.Group>
                <div className="d-flex justify-content-center">
                  <Button size="md" variant="primary" type="submit">
                    Create my account
                  </Button>
                </div>
              </div>
            ) : (
              <div>
                <Form.Group className="mb-3" controlId="formBasicCheckbox">
                  <Form.Check type="checkbox" label="Remember my email" />
                </Form.Group>
                <div className="d-flex justify-content-center">
                  <Button size="md" variant="primary" type="submit">
                    {isGoal ? "Add goal" : "Add transaction"}
                  </Button>
                </div>
              </div>
            )}
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <div
            style={{
              hover: {
                cursor: "pointer",
              },
            }}
            className="d-flex mt-3 border-0 justify-content-center fw-bold text-secondary"
          >
            <Button
              className="border-0"
              onClick={() => handleClose}
              variant="outline-secondary"
            >
              Go back
            </Button>
          </div>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default AddTransacGoal;
