import React, { useEffect, useState } from "react";
import { Bar, Line, Chart, Doughnut } from "react-chartjs-2";
import { Chart as ChartJS } from "chart.js/auto";
import Transaction from "../Components/Transaction";
import { Card, Button, Tooltip, OverlayTrigger } from "react-bootstrap";
import MoneyCard from "../Components/Cards/MoneyCard";
import DoughnutCard from "../Components/Cards/DoughnutCard";
import BarCard from "../Components/Cards/BarCard";
import Goals from "../Components/Goals";
import AddTransactionButton from "../Components/AddTransactionButton";

const state = {
  labels: ["January", "February", "March", "April", "May"],
  datasets: [
    {
      label: "Rainfall",
      backgroundColor: "rgba(75,192,192,1)",
      borderColor: "rgba(0,0,0,1)",
      borderWidth: 2,
      data: [65, 59, 80, 81, 56],
    },
  ],
};

const doughnutData = {
  labels: ["Red", "Blue", "Yellow"],
  datasets: [
    {
      label: "My First Dataset",
      data: [300, 50, 100],
      backgroundColor: [
        "rgb(255, 99, 132)",
        "rgb(54, 162, 235)",
        "rgb(255, 205, 86)",
      ],
      hoverOffset: 4,
    },
  ],
};

const Home = () => {
  let [transactions, setTransactions] = useState([]);
  let [balance, setBalance] = useState([]);

  useEffect(() => {
    console.log("hola");
    getTransactions();
  }, []);

  let getTransactions = async () => {
    let response = await fetch("/api/transactions/");
    let data = await response.json();
    setTransactions(data);
  };

  let financialData = [
    { spend: 245.0, date: "01-01-2022" },
    { spend: 821.0, date: "03-01-2022" },
    { spend: 447.0, date: "04-01-2022" },
  ];

  const renderTooltip = (props) => (
    <Tooltip id="button-tooltip" {...props}>
      Add Transaction
    </Tooltip>
  );

  return (
    <div className="d-flex justify-content-center my-5">
      <AddTransactionButton />
      <div className="row px-0" style={{ width: "95%", backgroundColor: "" }}>
        <div
          className="mx-0 row col-lg-9 col-md-12 col-sm-12"
          style={{ backgroundColor: "" }}
        >
          <div className="" style={{ backgroundColor: "" }}>
            <div className="" style={{ backgroundColor: "" }}>
              <div className="">
                <div className="row ">
                  <div className="col-lg-3 col-md-3 col-sm-5">
                    <MoneyCard
                      amount={transactions["balance"]}
                      title={"Balance"}
                    />
                  </div>
                  <div className="col-lg-3 col-md-3 col-sm-5">
                    <MoneyCard
                      amount={transactions["income"]}
                      title={"Income"}
                    />
                  </div>
                  <div className="col-lg-3 col-md-3 col-sm-5">
                    <MoneyCard amount={"40.00"} title={"Savings"} />
                  </div>
                  <div className="col-lg-3 col-md-3 col-sm-5">
                    <MoneyCard
                      amount={transactions["expenses"]}
                      title={"Expenses"}
                    />
                  </div>
                </div>
              </div>
            </div>
            <div className="my-3">
              <div className="row align-items-top justify-content-center">
                <div className="col-lg-6 col-md-6 col-sm-11 my-2">
                  <BarCard
                    title={"Activity"}
                    data={state}
                    explanation={"Expenses group by category"}
                  />
                </div>
                <div className="col-lg-6 col-md-6 col-sm-11 my-2">
                  <DoughnutCard
                    title={"Expenses"}
                    data={doughnutData}
                    explanation={"Expenses group by category"}
                  />
                </div>
              </div>
            </div>
            <div className="my-5">
              <div className="row align-items-top justify-content-center">
                <div
                  className="col-lg-3 col-md-10 col-sm-11"
                  style={{ backgroundColor: "" }}
                >
                  <div className="py-0 col-lg-12 col-md-12 col-sm-12">
                    <MoneyCard amount={"90.00"} title={"Expenses"} />
                  </div>
                </div>
                <div
                  className="col-lg-9 col-md-10 col-sm-11"
                  style={{ backgroundColor: "" }}
                >
                  <Card className="mb-5 w-100 d-flex">
                    <div className="mt-5 mb-3 mx-3 justify-content-center">
                      <div className="flex-column">
                        <div className="pb-2 h4 text-right">
                          Recent Transactions
                        </div>
                        <div className="d-flex justify-content-center">
                          <div className="w-100" style={{}}>
                            <Transaction loadPagination={false} />
                          </div>
                        </div>
                      </div>
                    </div>
                  </Card>
                </div>
              </div>
            </div>
          </div>
        </div>
        <Goals />
      </div>
    </div>
  );
};

export default Home;
