                </div>
                <div className="col-lg-3 col-md-3 col-sm-5">
                  <MoneyCard amount={"$90.00"} title={"Expenses"} />
                </div>
              </div>
            </div>
          </div>
          <div className="my-3">
            <div className="row align-items-top justify-content-center">
              <div className="col-lg-6 col-md-10 col-sm-11 my-2">
                <BarCard
                  title={"Activity"}
                  data={state}
                  explanation={"Expenses group by category"}
                />
              </div>
              <div className="col-lg-6 col-md-10 col-sm-11 my-2">
                <DoughnutCard
