import React, { useEffect, useContext } from "react";
import { Card, ProgressBar } from "react-bootstrap";
import AuthContext from "../Context/AuthContext";

const MoneyCard = ({ amount, title, saved, id }) => {
  const completed = (saved * 100) / amount;
  let { authTokens, logoutUser } = useContext(AuthContext);

  let deleteGoals = async () => {
    fetch(`/api/goals/${id}/delete/`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + String(authTokens.access),
      },
    });
    window.location.reload();
  };

  return (
    <div>
      <Card className="rounded-5 my-2">
        <Card.Body className="mx-2 bg-red">
          {saved ? (
            <div className="">
              <div className="d-flex mb-2">
                <div className="col-md-11 col-sm-11 col-ld-11">
                  <Card.Text className="text-capitalize">{title}</Card.Text>
                </div>
                <div className="col-md-11 col-sm-11 col-ld-11">
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="16"
                    height="16"
                    fill="currentColor"
                    className="bi bi-trash2"
                  >
                    <path d="M14 3a.702.702 0 0 1-.037.225l-1.684 10.104A2 2 0 0 1 10.305 15H5.694a2 2 0 0 1-1.973-1.671L2.037 3.225A.703.703 0 0 1 2 3c0-1.105 2.686-2 6-2s6 .895 6 2zM3.215 4.207l1.493 8.957a1 1 0 0 0 .986.836h4.612a1 1 0 0 0 .986-.836l1.493-8.957C11.69 4.689 9.954 5 8 5c-1.954 0-3.69-.311-4.785-.793z" />
                  </svg>
                </div>
              </div>
              <div>
                <h4>
                  <span className="text-success">${saved}</span>
                  <span> / $ {amount}</span>
                </h4>
                <ProgressBar variant="success" now={completed} />
              </div>
            </div>
          ) : (
            <div>
              <Card.Text className="text-capitalize">{title}</Card.Text>
              <div>
                {amount > 0 ? (
                  <h4>${amount}</h4>
                ) : (
                  <h4 className="text-danger">${amount}</h4>
                )}
              </div>
            </div>
          )}
        </Card.Body>
      </Card>
    </div>
  );
};

export default MoneyCard;
