import React from "react";
import { Bar, Line, Chart, Doughnut } from "react-chartjs-2";
import { Chart as ChartJS } from "chart.js/auto";
import Transaction from "../Transaction";
import { Card } from "react-bootstrap";
import MoneyCard from "../Cards/MoneyCard";
import DoughnutCard from "../Cards/DoughnutCard";
import BarCard from "../Cards/BarCard";

const state = {
  labels: ["January", "February", "March", "April", "May"],
  datasets: [
    {
      label: "Rainfall",
      backgroundColor: "rgba(75,192,192,1)",
      borderColor: "rgba(0,0,0,1)",
      borderWidth: 2,
      data: [65, 59, 80, 81, 56],
    },
  ],
};

const doughnutData = {
  labels: ["Red", "Blue", "Yellow"],
  datasets: [
    {
      label: "My First Dataset",
      data: [300, 50, 100],
      backgroundColor: [
        "rgb(255, 99, 132)",
        "rgb(54, 162, 235)",
        "rgb(255, 205, 86)",
      ],
      hoverOffset: 4,
    },
  ],
};

const Home = () => {
  let financialData = [
    { spend: 245.0, date: "01-01-2022" },
    { spend: 821.0, date: "03-01-2022" },
    { spend: 447.0, date: "04-01-2022" },
  ];

  return (
    <div className="d-flex justify-content-center mt-5">
      <div className="flex-column" style={{ width: "90%" }}>
        <div className="row justify-content-around">
          <div className="col-lg-3 col-md-3 col-sm-5">
            <MoneyCard amount={"$532.00"} title={"Balance"} />
          </div>
          <div className="col-lg-3 col-md-3 col-sm-5">
            <MoneyCard amount={"$160.00"} title={"Income"} />
          </div>
          <div className="col-lg-3 col-md-3 col-sm-5">
            <MoneyCard amount={"$40.00"} title={"Savings"} />
          </div>
          <div className="col-lg-3 col-md-3 col-sm-5">
            <MoneyCard amount={"$90.00"} title={"Expenses"} />
          </div>
        </div>
        <div>
          <Card className="w-50 rounded-5">
            <Card.Body className="mx-2">
              <Card.Text>
                <h3>Activity</h3>
              </Card.Text>
              <Bar
                data={state}
                options={{
                  plugins: {
                    title: {
                      display: true,
                      text: "Average Rainfall per month",
                      fontSize: 20,
                    },
                    legend: {
                      display: true,
                      position: "right",
                    },
                  },
                }}
              />
            </Card.Body>
          </Card>
        </div>
        <div>
          <Card className="w-50 rounded-5">
            <Card.Body className="mx-2">
              <Card.Text>
                <h3>Activity</h3>
              </Card.Text>
              <Doughnut
                data={doughnutData}
                options={{
                  plugins: {
                    title: {
                      display: true,
                      text: "Average Rainfall per month",
                      fontSize: 20,
                    },
                    legend: {
                      display: true,
                      position: "right",
                    },
                  },
                }}
              />
            </Card.Body>
          </Card>
        </div>

        <div className="my-5 d-flex justify-content-center">
          <div className="flex-column w-75">
            <div className="pb-2 text-right">
              <h3>Transactions</h3>
            </div>
            <div className="d-flex justify-content-center">
              <div className="w-100" style={{}}>
                <Transaction />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
