import React, { useEffect, useState } from "react";
import { Button } from "react-bootstrap";
import MoneyCard from "./Cards/MoneyCard";
import AddTransacGoal from "./Forms/AddTransacGoal";

const Goals = () => {
  let [goals, setGoals] = useState([]);

  useEffect(() => {
    getGoals();
  }, []);

  let getGoals = async () => {
    let response = await fetch("/api/goals/");
    let data = await response.json();
    console.log(data);
    setGoals(data);
  };

  return (
    <div className="mx-0 col-lg-3" style={{ backgroundColor: "" }}>
      <div className="">
        <div className="row justify-content-around">
          <div className="my-2 col-lg-12 col-md-3 col-sm-5">
            <h3>Goals</h3>
          </div>
          <div className="my-2 col-lg-12 col-md-3 col-sm-5">
            {goals.map((goals, index) => {
              <div key={index}>
                <MoneyCard
                  saved={goals.saved}
                  amount={goals.amount}
                  title={goals.title}
                />
              </div>;
            })}
          </div>
          <div
            className="row mt-2 mb-5 col-lg-12 col-md-3 col-sm-5 d-flex justify-content-center"
            style={{ backgroundColor: "" }}
          >
            <AddTransacGoal goal={true} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Goals;
