import { Modal } from "bootstrap";
import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";

const AddTransacGoal = ({ goal }) => {
  const [isGoal, setIsGoal] = useState(goal);
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const submitForm = () => {
    if (isGoal) {
      console.log("Added Goal");
    } else {
      console.log("Added Transaction");
    }
  };
  return (
    <div>
      <div className="row mt-2 mb-5 col-lg-12 col-md-3 col-sm-5 d-flex justify-content-center">
        <Button size="lg" variant="outline-info" onClick={handleShow}>
          Add Goal
        </Button>
      </div>
      <Modal show={show} onHide={handleClose}>
        {isGoal ? (
          <Modal.Header closeButton>
            <Modal.Title>Add new goal</Modal.Title>
          </Modal.Header>
        ) : (
          <Modal.Header closeButton>
            <Modal.Title>Add new transaction</Modal.Title>
          </Modal.Header>
        )}
        <Modal.Body>
          <Form onSubmit={submitForm}>
            <div>
              <Form.Group className="mb-3" controlId="formReceiver">
                <Form.Label>Receiver </Form.Label>
                <Form.Control type="name" placeholder="Enter the receiver" />
                <Form.Text></Form.Text>
              </Form.Group>
            </div>
            <Form.Group className="mb-3" controlId="formAmount">
              <Form.Label>Amount</Form.Label>
              <Form.Control type="number" placeholder="Enter amount" />
              <Form.Text className="text-muted">
                {isGoal ? "We'll never share your email with anyone else." : ""}
              </Form.Text>
            </Form.Group>
            {isGoal ? (
              <div></div>
            ) : (
              <div>
                <div>
                  <Form.Group className="mb-3" controlId="formCategory">
                    <Form.Label>Category </Form.Label>
                    <Form.Control
                      type="name"
                      placeholder="Enter the category"
                    />
                    <Form.Text></Form.Text>
                  </Form.Group>
                </div>
                <div>
                  <Form.Group className="mb-3" controlId="formDate">
                    <Form.Label>Date </Form.Label>
                    <Form.Control type="date" placeholder="Enter the date" />
                    <Form.Text></Form.Text>
                  </Form.Group>
                </div>
              </div>
            )}
            <div>
              <div className="d-flex justify-content-center">
                <Button size="md" variant="primary" type="submit">
                  {isGoal ? "Add goal" : "Add transaction"}
                </Button>
              </div>
            </div>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <div
            style={{
              hover: {
                cursor: "pointer",
              },
            }}
            className="d-flex mt-3 border-0 justify-content-center fw-bold text-secondary"
          >
            <Button
              className="border-0"
              onClick={() => handleClose}
              variant="outline-secondary"
            >
              Go back
            </Button>
          </div>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default AddTransacGoal;
