import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";

const SignForm = ({ register }) => {
  const [isRegister, setIsRegister] = useState(register);
  return (
    <div>
      <Form>
        {isRegister ? (
          <div>
            <Form.Group className="mb-3" controlId="formBasicName">
              <Form.Label>Name </Form.Label>
              <Form.Control type="name" placeholder="Enter your name" />
            </Form.Group>
          </div>
        ) : (
          <div></div>
        )}
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control type="email" placeholder="Enter email" />
          <Form.Text className="text-muted">
            {isRegister ? "We'll never share your email with anyone else." : ""}
          </Form.Text>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control type="password" placeholder="Password" />
          <Form.Text className="text-muted">
            {isRegister ? "Needs to have at least 8 characters." : ""}
          </Form.Text>
        </Form.Group>
        {isRegister ? (
          <div>
            <Form.Group className="mb-3" controlId="formBasicCheckbox">
              <Form.Check
                type="checkbox"
                label="I accept terms and conditions"
              />
            </Form.Group>
            <div className="d-flex justify-content-center">
              <Button size="md" variant="primary" type="submit">
                Create my account
              </Button>
            </div>
          </div>
        ) : (
          <div>
            <Form.Group className="mb-3" controlId="formBasicCheckbox">
              <Form.Check type="checkbox" label="Remember my email" />
            </Form.Group>
            <div className="d-flex justify-content-center">
              <Button size="md" variant="primary" type="submit">
                Login to my account
              </Button>
            </div>
          </div>
        )}
        <div
          style={{
            hover: {
              cursor: "pointer",
            },
          }}
          className="d-flex mt-3 border-0 justify-content-center fw-bold text-secondary"
        >
          <Button
            className="border-0"
            onClick={() => setIsRegister(!isRegister)}
            variant="outline-secondary"
          >
            {isRegister
              ? "Don't have an account? Login here"
              : "Already have an account? Login here"}
          </Button>
        </div>
      </Form>
    </div>
  );
};

export default SignForm;
