import React, { useState } from "react";
import {
  Form,
  Button,
  Modal,
  InputGroup,
  FormControl,
  OverlayTrigger,
  Tooltip,
} from "react-bootstrap";

const AddTransacGoal = ({ goal, history }) => {
  const [isGoal, setIsGoal] = useState(goal);
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const renderTooltip = (props) => (
    <Tooltip id="button-tooltip" {...props}>
      Add Transaction
    </Tooltip>
  );

  const [validated, setValidated] = useState(false);

  const createTransaction = async (event) => {
    /*console.log(event.target.elements.formReceiver.value);
    console.log(event.target.elements.formAmount.value);
    console.log(event.target.elements.formCategory.value);
    console.log(event.target.elements.formDate.value);
    console.log(event.target.elements.formWay.value);*/
    fetch("/api/transactions/create/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        amount: event.target.elements.formAmount.value,
        category: event.target.elements.formCategory.value,
        date: event.target.elements.formDate.value,
        receiver: event.target.elements.formReceiver.value,
        transaction_way: event.target.elements.formWay.value,
      }),
    });
  };

  const createGoal = async (event) => {
    console.log(event.target.elements.formReceiver.value);
    console.log(event.target.elements.formAmount.value);
  };

  const handleSubmit = async (event) => {
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      console.log(form.checkValidity());
      event.preventDefault();
      event.stopPropagation();
    } else {
      if (isGoal) {
        createGoal(event);
      } else {
        createTransaction(event);
      }
    }
    setValidated(true);
    handleClose();
  };

  const handleCloseForm = (event) => {
    event.preventDefault();
    event.stopPropagation();
    setValidated(false);
    handleClose();
  };

  return (
    <div className="px-0 d-flex justify-content-center mx-0">
      <Modal show={show} onHide={handleClose}>
        {isGoal ? (
          <Modal.Header closeButton onClick={() => setValidated(false)}>
            <Modal.Title>Add new goal</Modal.Title>
          </Modal.Header>
        ) : (
          <Modal.Header closeButton onClick={() => setValidated(false)}>
            <Modal.Title>Add new transaction</Modal.Title>
          </Modal.Header>
        )}
        <Form
          noValidate
          validated={validated}
          onSubmit={(e) => handleSubmit(e)}
          onReset={(e) => handleCloseForm(e)}
        >
          <Modal.Body>
            <div>
              <Form.Group className="mb-3" controlId="formReceiver">
                <Form.Label>Receiver </Form.Label>
                <Form.Control
                  required
                  type="text"
                  placeholder="Enter the receiver"
                />
                <Form.Control.Feedback type="invalid">
                  Please enter a receiver.
                </Form.Control.Feedback>
                <Form.Text></Form.Text>
              </Form.Group>
            </div>
            <Form.Group className="mb-3" controlId="formAmount">
              <Form.Label>Amount</Form.Label>
              <InputGroup className="mb-3">
                <InputGroup.Text>$</InputGroup.Text>
                <FormControl
                  required
                  type="number"
                  min="0.00"
                  max="10000.00"
                  step="0.01"
                  placeholder="Enter amount"
                />
                <Form.Control.Feedback type="invalid">
                  Please enter a amount.
                </Form.Control.Feedback>
              </InputGroup>
            </Form.Group>
            {isGoal ? (
              <div></div>
            ) : (
              <div>
                <div>
                  <Form.Group className="mb-3" controlId="formCategory">
                    <Form.Label>Category </Form.Label>
                    <Form.Control
                      required
                      type="name"
                      placeholder="Enter the category"
                    />
                    <Form.Control.Feedback type="invalid">
                      Please enter a category.
                    </Form.Control.Feedback>
                    <Form.Text></Form.Text>
                  </Form.Group>
                </div>
                <div>
                  <Form.Group className="mb-3" controlId="formDate">
                    <Form.Label>Date </Form.Label>
                    <Form.Control
                      required
                      type="date"
                      placeholder="Enter the date"
                    />
                    <Form.Control.Feedback type="invalid">
                      Please choose a date.
                    </Form.Control.Feedback>
                    <Form.Text></Form.Text>
                  </Form.Group>
                </div>
                <div>
                  <Form.Group className="mb-3" controlId="formWay">
                    <Form.Label>Transaction Way </Form.Label>
                    <Form.Control as="select" type="name" required>
                      <option value="I">In</option>
                      <option value="O">Out</option>
                    </Form.Control>
                    <Form.Control.Feedback type="invalid">
                      Please enter a way.
                    </Form.Control.Feedback>
                  </Form.Group>
                </div>
              </div>
            )}
          </Modal.Body>
          <Modal.Footer>
            <div>
              <div className="d-flex justify-content-center">
                <Button size="md" variant="primary" type="submit">
                  {isGoal ? "Add goal" : "Add transaction"}
                </Button>
              </div>
            </div>
            <div
              style={{
                hover: {
                  cursor: "pointer",
                },
              }}
              className="d-flex border-0 justify-content-center fw-bold text-secondary"
            >
              <Button
                className="border-0"
                type="reset"
                variant="outline-secondary"
              >
                Go back
              </Button>
            </div>
          </Modal.Footer>
        </Form>
      </Modal>
      {isGoal ? (
        <div className="row mb-5 px-0 col-lg-12 col-md-3 col-sm-5 d-flex justify-content-center">
          <Button size="lg" variant="outline-info" onClick={handleShow}>
            Add Goal
          </Button>
        </div>
      ) : (
        <div className="d-flex  justify-content-end ">
          <div className="d-flex z-0 fixed-bottom justify-content-end me-4 mb-4">
            <OverlayTrigger
              placement="left"
              delay={{ show: 250, hide: 400 }}
              overlay={renderTooltip}
            >
              <Button
                onClick={handleShow}
                style={{ borderRadius: "50%" }}
                size="md"
                variant="primary"
              >
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="28"
                  height="40"
                  fill="currentColor"
                  className="bi bi-plus-lg"
                  viewBox="0 0 16 16"
                >
                  <path
                    fillRule="evenodd"
                    d="M8 2a.5.5 0 0 1 .5.5v5h5a.5.5 0 0 1 0 1h-5v5a.5.5 0 0 1-1 0v-5h-5a.5.5 0 0 1 0-1h5v-5A.5.5 0 0 1 8 2Z"
                  />
                </svg>
              </Button>
            </OverlayTrigger>
          </div>
        </div>
      )}
    </div>
  );
};

export default AddTransacGoal;
