import React from "react";
import { Bar, Line, Chart } from "react-chartjs-2";
import { Chart as ChartJS } from "chart.js/auto";
import Transaction from "../Transaction";

const state = {
  labels: ["January", "February", "March", "April", "May"],
  datasets: [
    {
      label: "Rainfall",
      backgroundColor: "rgba(75,192,192,1)",
      borderColor: "rgba(0,0,0,1)",
      borderWidth: 2,
      data: [65, 59, 80, 81, 56],
    },
  ],
};

const Home = () => {
  let financialData = [
    { spend: 245.0, date: "01-01-2022" },
    { spend: 821.0, date: "03-01-2022" },
    { spend: 447.0, date: "04-01-2022" },
  ];

  return (
    <div className="d-flex justify-content-center">
      <div className="flex-column" style={{ width: "90%" }}>
        <div style={{}}>
          <Bar
            data={state}
            options={{
              plugins: {
                title: {
                  display: true,
                  text: "Average Rainfall per month",
                  fontSize: 20,
                },
                legend: {
                  display: true,
                  position: "right",
                },
              },
            }}
          />
        </div>
        <div
          className="d-flex justify-content-center"
          style={{ backgroundColor: "red" }}
        >
          <div
            className="d-flex justify-content-center flex-column w-100"
            style={{ backgroundColor: "blue" }}
          >
            <div className="pb-2 text-center">
              <h3>Transactions</h3>
            </div>
            <div className="d-flex justify-content-center w-75" style={{}}>
              <Transaction />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
